function LRC(str) {
  for (var bytes = [], lrc = 0, i = 0; i < str.length; i++)
    bytes.push(parseInt(str.substr(2 * i, 2), 16));
  let blen = bytes.length / 2;
  for (var j = 0; j < blen; j++) lrc = (lrc + bytes[j]) & 255;
  return {
    lrc: (lrc = (1 + (255 ^ lrc)) & 255),
    hex: lrc.toString(16),
    bytes: bytes,
  };
}
function write(MAC, msgID, adr, no, data) {
